/* Give the document some style */
div {
  padding: 20px;
  border: 1px solid #eee;
  border-radius: 4px;
}

div:first-child {
  border-bottom: 0;
}

// Every node module is available to you in your Gistbooks
var math = require('mathjs');
var $ = require('jquery');

function norm(x, y) {
  return math.sqrt(math.square(x) + math.square(y));
}

$('.output').text(norm(3, 4));
